START TRANSACTION;

DROP TABLE IF EXISTS landmark CASCADE;
DROP TABLE IF EXISTS users CASCADE;
DROP TABLE IF EXISTS itinerary_landmark CASCADE;
DROP TABLE IF EXISTS itinerary CASCADE;
DROP TABLE IF EXISTS itinerary_user CASCADE;
DROP TABLE IF EXISTS favorites CASCADE;

CREATE TABLE landmark (
landmark_id serial,
landmark_img_url varchar (10000) NOT NULL,
landmark_description varchar (100000) NOT NULL,
landmark_name varchar (1000) NOT NULL,
landmark_type varchar (1000) NOT NULL,
landmark_latitude decimal (16,14) NOT NULL,
landmark_longitude decimal (16,14) NOT NULL,
Monday_open time,
Monday_close time,
Tuesday_open time,
Tuesday_close time,
Wednesday_open time,
Wednesday_close time,
Thursday_open time,
Thursday_close time,
Friday_open time,
Friday_close time,
Saturday_open time, 
Saturday_close time,
Sunday_open time,
Sunday_close time,
kid_friendly boolean NOT NULL,
admission boolean NOT NULL,
restaurant_type varchar (1000),
is_outdoor boolean NOT NULL,
landmark_rating numeric (6,2),
CONSTRAINT PK_landmark_id PRIMARY KEY (landmark_id)
);

CREATE TABLE users (
user_id SERIAL,
username varchar(50) NOT NULL UNIQUE,
password_hash varchar(200) NOT NULL,
role varchar(50) NOT NULL,
CONSTRAINT PK_user PRIMARY KEY (user_id)
);

CREATE TABLE itinerary_landmark (
itinerary_id int NOT NULL,
landmark_id int NOT NULL
);

CREATE TABLE itinerary (
itinerary_id serial,
itinerary_title varchar (1000) NOT NULL,
itinerary_date date NOT NULL,
starting_time time,
CONSTRAINT PK_itinerary PRIMARY KEY (itinerary_id)
);

CREATE TABLE itinerary_user (
itinerary_id int NOT NULL,
user_id int NOT NULL
);

CREATE TABLE favorites (
favorites_id serial,
landmark_img_url varchar (10000) NOT NULL,
landmark_description varchar (100000) NOT NULL,
landmark_name varchar (1000) NOT NULL,
landmark_type varchar (1000) NOT NULL,
landmark_latitude decimal (16,14) NOT NULL,
landmark_longitude decimal (16,14) NOT NULL,
Monday_open time,
Monday_close time,
Tuesday_open time,
Tuesday_close time,
Wednesday_open time,
Wednesday_close time,
Thursday_open time,
Thursday_close time,
Friday_open time,
Friday_close time,
Saturday_open time, 
Saturday_close time,
Sunday_open time,
Sunday_close time,
kid_friendly boolean NOT NULL,
admission boolean NOT NULL,
restaurant_type varchar (1000),
is_outdoor boolean NOT NULL,
landmark_rating numeric (6,2),
CONSTRAINT PK_favorites_id PRIMARY KEY (favorites_id)
);
COMMIT;

